import { BtCate } from "../components/ui/buttons";
import { Swiper, SwiperSlide } from "swiper/react"

import { Navigation } from 'swiper/modules';
import "swiper/css";
import "swiper/css/navigation";

import "../styles/tour.css";
import "../styles/common.css";
import { useEffect, useRef, useState } from "react";
import axios from "axios";
import styled from "@emotion/styled";
import { InnerArea, SectionTag } from "./layout/layout";

function numberWithCommas(number) {
    return number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

function Tour(){
    //js 코드 자리
    //JSX 의 요소를 React 에서 참조
    const swiperRef = useRef();

    const [htmlTag, setHtmlTag] = useState([]);

    const axiosJsonData = () => {
        axios
        .get("https://port-0-interpark-server-7lk2blopj0m0a.sel5.cloudtype.app/tour")
        .then(function(res){
            console.log(res.data);

            const result = res.data;    
            let arr = [];
            for(let i = 0; i < result.total; i++) {
                const obj = res.data["tour_" + (i+ 1)];

                arr[i] = obj;
            }    
            console.log(arr);
            setHtmlTag(arr);
        })
        .catch(function(error){
            console.log(error);
            const dmObj = {

                    "total": 9,
                    "tour_1": {
                        "image": "images/t1.jpeg",
                        "state": "히트상품",
                        "title": "사이판 최대 워터파크 웨이브정글 이용가능",
                        "desc": "[사이판5일]사이판 월드리조트_골드카드",
                        "price": "1069000",
                        "url": "a.html"
                    },
                    "tour_2": {
                        "image": "images/t2.jpeg",
                        "state": "베스트셀러",
                        "title": "최대판매 상품",
                        "desc": "[부산-하노이 5일] 가족여행최고 하노이/하롱베이+옌뜨 5...",
                        "price": "679000",
                        "url": "a.html"
                    },
                    "tour_3": {
                        "image": "images/t3.webp",
                        "state": "방콕",
                        "title": "수라삭 BTS 스카이트레인역 접근성 GOOD!",
                        "desc": "이스틴 그랜드 호텔 사톤",
                        "price": "160452",
                        "url": "a.html"
                    },
                    "tour_4": {
                        "image": "images/t4.jpeg",
                        "state": "BEST",
                        "title": "라스베가스 준특급 2박, 5대특식 포함",
                        "desc": "[미서부/단독/BEST] 3대도시+8대캐년 완전일주 10...",
                        "price": "3399000",
                        "url": "a.html"
                    },
                    "tour_5": {
                        "image": "images/t5.webp",
                        "state": "괌",
                        "title": "공항 10분 거리, 오션뷰 인피니티 풀",
                        "desc": "괌 리프 호텔 (구.괌 리프 앤 올리브 스파 리조트)",
                        "price": "219120",
                        "url": "a.html"
                    },
                    "tour_6": {
                        "image": "images/t6.webp",
                        "state": "히트상품",
                        "title": "공항 15분 거리, 논느억 해변에 위치",
                        "desc": "빈펄 나트랑 베이 리조트 & 빌라",
                        "price": "124592",
                        "url": "a.html"
                    },
                    "tour_7": {
                        "image": "images/t7.jpeg",
                        "state": "강력특가",
                        "title": "디럭스 킹, 정원뷰",
                        "desc": "롤링힐스 호텔",
                        "price": "1690000",
                        "url": "a.html"
                    },
                    "tour_8": {
                        "image": "images/t8.jpeg",
                        "state": "강력특가",
                        "title": "패밀리 투룸 로프트(21평)",
                        "desc": "어반스테이 여수웅천",
                        "price": "63900",
                        "url": "a.html"
                    },
                    "tour_9": {
                        "image": "images/t9.jpeg",
                        "state": "강력특가",
                        "title": "클래식 킹",
                        "desc": "레스케이프 호텔",
                        "price": "220000",
                        "url": "a.html"
                    }

            }
            let arr = [];
            for (let i = 0; i < dmObj.total; i++) {
              const obj = dmObj["tour_" + (i + 1)];
              arr[i] = obj;
            }
            setHtmlTag(arr);


        });
    }
    // axios ("tour.json")
    useEffect(()=> {
        axiosJsonData();
    }, [])
    return (
        <SectionTag pt={0} pb={90}>
                <InnerArea>
                    <div className="tour-header">
                        <h2 className="tour-title">투어 특가</h2>
                        <span className="tour-txt">해외여행은 인터파크다</span>
                    </div>
                    <div className="tour-main">
                        <div className="tour-category">
                            <ul className="tour-list">
                                <li>
                                    <BtCate active={true}>망설이면 품절</BtCate>
                                </li>
                                <li>
                                    <BtCate>패키지</BtCate>
                                </li>
                                <li>
                                    <BtCate>국내숙소</BtCate>
                                </li>
                                <li>
                                    <BtCate>해외숙소</BtCate>
                                </li>
                            </ul>
                        </div>
                        <div className="tour-slide-wrap">
                        {/* <!-- Swiper --> */}
                        <Swiper 
                    slidesPerView={3} 
                    spaceBetween={28} 
                    slidesPerGroup={3} 
                    onSwiper={(swiper)=>{
                        swiperRef.current= swiper;
                    }}
                    modules={[Navigation]} 
                    navigation={{
                        nextEl: ".tour-slide-wrap .slide-next-bt",
                        prevEl: ".tour-slide-wrap .slide-prev-bt",
                    }}
                    className="tour-slide">

                    {htmlTag.map((item, index) => {
                        return(
                          <SwiperSlide key={index}>
                            {index === htmlTag.length - 1 ? (
                                <a href={item.url}>바로가기</a>
                                ) : (
                                <div className="swiper-slide">
                                    <div className="tour-slide-item">
                                  <a href={item.url} className="tour-link">
                                    <div className="tour-img">
                                      <img src={item.image} alt={item.desc} />
                                    </div>
                                    <div className="tour-state">{item.state}</div>
                                    <div className="tour-info">
                                      <ul className="tour-good-list">
                                        <li>
                                            <span className="tour-good-info-title">
                                                <div>{item.title}</div>
                                            </span>
                                            <p className="tour-good-info-desc">
                                            {item.desc}
                                            </p>
                                        </li>
                                        <li>
                                          <span className="tour-good-info-price">
                                          <em>{numberWithCommas(item.price)}</em>
                                          원~
                                          </span>
                                        </li>
                                      </ul>
                                    </div>
                                  </a>
                                </div>
